import React, {useContext, useEffect, useState} from 'react';
import {Button,  Dropdown, Form,  Modal, Row} from "react-bootstrap";
import {Context} from "../../index";
import app from "../../App";
import {
    createApplication,
    fetchApplications,
    fetchDate, fetchTypes,
    fetchVersion
} from "../../http/deviceAPI";
import {observer} from "mobx-react-lite";

const CreateLicense = observer(({show, onHide}) => {
    const {license} = useContext(Context)
    const [name, setName] = useState('')
    const {application} = useContext(Context)

    useEffect(() => {
        fetchTypes().then(data=> application.setTypes(data))
        fetchApplications().then(data=> license.setApplications(data))
        fetchVersion().then(data=> license.setVersion(data))
        fetchDate().then(data=> license.setDate(data))
    }, [])
    console.log(license.types)

    const addLicense = () => {
        const formData = new FormData()
        formData.append('licenseApp', name)
        formData.append('applicationId', license.selectedApplication.id)
        formData.append('VersionId', license.selectedVersion.id)
        formData.append('DateOfActionId', license.selectedDate.id)
        formData.append('typeId', application.selectedType.id)
        createApplication(formData).then(data => onHide())
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить лицензию
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>

                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle  className={"btn-secondary"}>{application.selectedType.name || "Выберите тип"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {application.types.map(type =>
                                <Dropdown.Item onClick={() => application.setSelectedType(type)}
                                               key={type.id}
                                >
                                    {type.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-2"
                        placeholder="Введите название лицензии">
                    </Form.Control>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addLicense}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateLicense;
