import React from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import bigStar from '../assets/bigStar.png'
const ApplicationPage = () => {
    const application =  {id: 1, name: "KasumovaApplication", price: 2500, rating: 5, img: "https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png" }
    const description = [
        {id:1, title: ' title1', description: 'd1'},
        {id:2, title: ' title2', description: 'd2'},
        {id:3, title: ' title3', description: 'd3'}
    ]



    return (
        <Container className="mt-3">
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={application.img}></Image>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{application.name}</h2>
                        <div className="d-flex align-items-center justify-content-center"
                             style={{background: `url(${bigStar}) no-repeat center center`, width:240, height:240, backgroundSize: 'cover', fontSize:64}}
                        >
                            {application.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                <Card className="d-flex flex-column align-items-center justify-content-around"
                style={{width:300, height:300, fontSize:32, border: '5px solid lightgray'}}
                >
                    <h3>{application.price}</h3>
                    <Button variant={"outline-dark"}>Добавить в корзину</Button>
                </Card>
                </Col>
            </Row>
           <Row>
               {description.map(info =>
                   <Row key={info.id}>
                       {info.title}:{info.description}
                   </Row>



               )}
           </Row>
        </Container>
    );
};

export default ApplicationPage;
