import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            {id:1, name: 'ПО для обучения'},
            {id:2, name: 'Управленческое ПО'},
            {id:3, name: 'Трансляторы'},
            {id:4, name: 'Составление смет'}
        ]
        this._developers = [
            {id:1, name: 'Kasumova'},
            {id:5, name: 'Diplom'},
            {id:6, name: 'DeveloperName'},
            {id:7, name: 'Developer4'}
        ]

        this._applications = [
            {id: 1, name: "KasumovaApplication", price: 2500, rating: 0, img: "https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png" },
            {id: 2, name: "ApplicationTwo", price: 1500, rating: 0, img: "https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png" },
            {id: 3, name: "Application3", price: 500, rating: 0, img: "https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png" },
            {id: 4, name: "Application4", price: 200, rating: 0, img: "https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png" }
        ]
        this._selectedType = {}
        this._selectedDeveloper = {}
        makeAutoObservable(this)
    }

    setTypes(types){
        this._types = types
    }
    setDevelopers(developers){
        this._developers = developers
    }
    setApplications(applications){
        this._applications = applications
    }

    setSelectedType(type) {
        this._selectedType = type
    }
    setSelectedDeveloper(developer) {
        this._selectedDeveloper = developer
    }
    get types(){
        return this._types
    }

    get developers(){
        return this._developers
    }
    get applications(){
        return this._applications
    }

    get selectedType(){
        return this._selectedType
    }
    get selectedDeveloper(){
        return this._selectedDeveloper
    }
}
