import React, {useContext, useState} from 'react';
import {Button, Dropdown, Form, Modal} from "react-bootstrap";
import {Context} from "../../index";

const CreateApplication = ({show, onHide}) => {
   const {application} = useContext(Context)
    const [info, setInfo] = useState([])

    const addInfo = () => {
      setInfo([...info, {title: '', description: '', number: Date.now()}])
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить приложение
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>

                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>Выберите тип</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {application.types.map(type =>
                           <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown>
                        <Dropdown.Toggle>Выберите разработчика</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {application.developers.map(developer =>
                                <Dropdown.Item key={developer.id}>{developer.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        className="mt-2"
                        placeholder="Введите название приложения">
                    </Form.Control>
                    <Form.Control
                        className="mt-2"
                        placeholder="Введите стоимость приложения"
                        type="number"

                    >
                    </Form.Control>
                    <Form.Control
                        className="mt-2"
                        type="file"
                    >
                    </Form.Control>
                    <hr/>
                    <Button
                    variant="outline-dark"
                    onClick={addInfo}
                    >
                        Добавить новое свойство</Button>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={onHide}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateApplication;
