import React, {useState} from 'react';
import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {createApplication, createOC} from "../../http/deviceAPI";

const CreateOC =  ({show, onHide}) => {
    const [value, setValue] = useState('')
    const [categoryOC, setCategory] = useState('')
    const addType = () => {

        const formData = new FormData()
        formData.append('name', value)
        formData.append('categoryOC', categoryOC)
        createApplication(formData).then(data => onHide())

        createOC({name: value}).then(data => {
            setValue('')
            onHide()
        })
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить операционную систему
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Row className="mt-4" key={i.number}>
                        <Col md={4}>
                            <Form.Control
                                value={value}
                                onChange={e => setValue(e.target.value)}
                                placeholder={"Введите название операционной системы"}
                            />
                        </Col>

                        <Col md={4}>
                            <Form.Control
                                value={value}
                                onChange={e => setCategory(e.target.value)}
                                placeholder={"Введите категорию операционной системы (при наличии)"}
                            />
                        </Col>
                    </Row>



                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addType}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateOC;
